select * from grss_user u where u.user_type in('user','coach') 


		and u.user_weibo = #{weibo}


		and u.user_weixin = #{weixin}


		and u.user_qq = #{qq}


		and u.user_phone = #{phone}

SELECT gu.* FROM grss_posts gp, `grss_posts_admire` gpa, `grss_user` gu WHERE gp.id = gpa.posts_id AND gpa.user_id = gu.user_id AND gp.`id` = #{postsId}
LIMIT #{offset},#{pageSize}
LIMIT 0,6

		select user_id,user_phone,nike_name,user_photo,user_sex,user_desc,user_type from grss_user
		where user_id in (select be_noticed_id from grss_follow where fans_id=#{userId})
  		limit #{offset},#{pageSize} ;

		select user_id,user_phone,nike_name,user_photo,user_sex,user_desc,user_type from grss_user
		where user_id in (select fans_id from grss_follow where be_noticed_id=#{userId})
  		limit #{offset},#{pageSize} ;

SELECT model.*, gc.club_id, gc.club_name, gco.id, gco. NAME, gco.price FROM grss_user model LEFT JOIN grss_club_coach gcc ON gcc.user_id = model.user_id LEFT JOIN grss_club gc ON gcc.club_id = gc.club_id LEFT JOIN grss_course gco ON gco.coach_id = model.user_id LEFT JOIN ( SELECT be_noticed_id,count(1) fs_count from grss_follow group by be_noticed_id) as fs_data ON model.user_id=fs_data.be_noticed_id WHERE model.user_type = "coach" and model.`status`=1
and gcc.club_id = #{clubId}
and model.user_sex = #{grssUser.userSex}
and model.nike_name like "%"#{grssUser.nikeName}"%"
order by fs_data.fs_count DESC limit #{offset},#{pageSize}

SELECT model.*, gc.club_id, gc.club_name, gco.id, gco. NAME, gco.price FROM grss_user model LEFT JOIN grss_club_coach gcc ON gcc.user_id = model.user_id LEFT JOIN grss_club gc ON gcc.club_id = gc.club_id LEFT JOIN grss_course gco ON gco.coach_id = model.user_id LEFT JOIN ( select course_id,count(1) as order_count from grss_order where order_date>=CONCAT(YEAR(curdate()),'-',DAY(curdate()),'-01 00:00:00') and `state`=2 GROUP BY course_id ) order_data ON gco.id=order_data.course_id WHERE model.user_type = "coach" and model.`status`=1
and gcc.club_id = #{clubId}
and gc.area =#{grssUser.area}
and model.user_sex = #{grssUser.userSex}
and model.nike_name like "%"#{grssUser.nikeName}"%"
ORDER BY order_data.order_count desc,model.reg_date DESC limit #{offset},#{pageSize}

SELECT model.*, gc.club_id, gc.club_name, gco.id, gco. NAME, gco.price, IFNULL(h.order_count,0) order_count FROM grss_user model LEFT JOIN grss_club_coach gcc ON gcc.user_id = model.user_id LEFT JOIN grss_club gc ON gcc.club_id = gc.club_id LEFT JOIN grss_course gco ON gco.coach_id = model.user_id LEFT JOIN ( SELECT gu.user_id, count(0) order_count FROM grss_user gu, grss_course gc, grss_order go WHERE gu.user_id = gc.coach_id and go.course_id = gc.id AND go.state = 2 AND DATE_FORMAT(go.order_date, '%Y%m') = DATE_FORMAT(now(), '%Y%m') GROUP BY gu.user_id ) h on model.user_id = h.user_id WHERE model.user_type = "coach" and model.`status` = 1
and gcc.club_id = #{clubId}
and model.user_sex = #{grssUser.userSex}
and model.nike_name like "%"#{grssUser.nikeName}"%"
ORDER BY order_count desc limit #{offset},#{pageSize}

  	SELECT 
  * 
FROM
  `grss_user` gu 
WHERE LEFT(gu.lng, LOCATE('.', gu.lng) - 1) = LEFT(
    #{lng},
    LOCATE('.', #{lng}) - 1
  ) 
  AND LEFT(gu.lat, LOCATE('.', gu.lat) - 1) = LEFT(
    #{lat},
    LOCATE('.', #{lat}) - 1
  )
  AND gu.user_id != #{userId}  

  	AND gu.user_sex=#{sex} 

  limit #{offset},#{pageSize}

SELECT * FROM grss_user gu WHERE gu.user_type = #{userType}

		and gu.`user_phone` = #{phone}


		and gu.`info_complete` = #{infoComplete}


		and gu.`status` = #{status} 


		and DATE_FORMAT(gu.reg_date,'%Y-%m-d') >= #{startDate}


		and DATE_FORMAT(gu.reg_date,'%Y-%m-d') <= #{endDate}

	order by gu.reg_date desc
 LIMIT #{offset},#{pageSize}

SELECT COUNT(0) FROM grss_user gu where gu.user_type = #{userType}

	 and gu.`user_phone` = #{phone}


	and gu.`info_complete` = #{infoComplete}


	and gu.`status` = #{status} 


	and DATE_FORMAT(gu.reg_date,'%Y-%m-d') >= #{startDate}


	and DATE_FORMAT(gu.reg_date,'%Y-%m-d') <= #{endDate}

SELECT * FROM grss_user gu WHERE  gu.`status` in(0,3)

		 and gu.`user_phone` = #{phone}


		and gu.`info_complete` = #{infoComplete}


		and DATE_FORMAT(gu.reg_date,'%Y-%m-d') >= #{startDate}


		and DATE_FORMAT(gu.reg_date,'%Y-%m-d') <= #{endDate}

	order by gu.reg_date desc
	LIMIT #{offset},#{pageSize}

SELECT count(0) FROM grss_user gu WHERE  gu.`status` in(0,3)

		 and gu.`user_phone` = #{phone}


		and gu.`info_complete` = #{infoComplete}


		and DATE_FORMAT(gu.reg_date,'%Y-%m-d') >= #{startDate}


		and DATE_FORMAT(gu.reg_date,'%Y-%m-d') <= #{endDate}
